import React, { useState, useEffect } from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet } from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import { RNCamera } from 'react-native-camera';
import TesseractOcr from 'tesseract.js';
import RNFS from 'react-native-fs';

const Acceuil = () => {
  const [image, setImage] = useState(null);
  const [text, setText] = useState('');

  useEffect(() => {
    const requestMediaLibraryPermissions = async () => {
      if (Platform.OS !== 'web') {
        const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
        if (status !== 'granted') {
          alert('Sorry, Permission denied!');
        }
      }
    };
    requestMediaLibraryPermissions();
  }, []);

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!result.cancelled) {
      setImage(result.uri);
      performOCR(result.uri);
    }
  };

  const performOCR = async (imageUri) => {
    const { uri } = await resizeImage(imageUri, 800, 600); // Resize image to improve OCR accuracy

    TesseractOcr.recognize(
      uri,
      'ENG', // Language code, adjust as needed
      { logger: (info) => console.log(info) }
    ).then(({ data: { text } }) => {
      setText(text);
    });
  };

  const resizeImage = async (imageUri, maxWidth, maxHeight) => {
    const image = await RNFS.readFile(imageUri, 'base64');
    const resizedImage = await ImageResizer.createResizedImage(
      `data:image/jpeg;base64,${image}`,
      maxWidth,
      maxHeight,
      'JPEG',
      100
    );

    return {
      uri: resizedImage.uri,
      width: resizedImage.width,
      height: resizedImage.height,
    };
  };

  return (
    <View style={styles.container}>
      <View style={styles.gendersContainer}>
        <TouchableOpacity style={styles.femaleContainer} onPress={pickImage}>
          <Image
            style={styles.femaleImage}
            source={require('../assets/galerie.png')}
          />
          <Text style={styles.femaleText}>Galerie</Text>
        </TouchableOpacity>
        {image && (
          <Image
            style={{ width: 200, height: 200, borderRadius: 100, marginBottom: 20 }}
            source={{ uri: image }}
          />
        )}
        {text !== '' && (
          <Text style={styles.ocrText}>{text}</Text>
        )}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    backgroundColor: '#1c1c1e',
    flexDirection: 'column',
    overflow: 'hidden',
    padding: 32,
    position: 'relative',
    width: 300
  },
  gendersContainer: {
    alignItems: 'center',
    flexDirection: 'column',
    marginBottom: 132,
    width: '87.35%',
  },
  femaleContainer: {
    alignItems: 'center',
    backgroundColor: '#2c2c2e',
    borderRadius: 70,
    flexDirection: 'column',
    padding: 37,
    width: '100%',
  },
  femaleImage: {
    height: 48,
    margin: 0,
    marginBottom: 19,
    objectFit: 'contain',
    width: 31.9,
  },
  femaleText: {
    color: '#ffffff',
    fontFamily: 'Open Sans, Source Sans Pro',
    fontSize: 18,
    fontWeight: '400',
    lineHeight: 1.4,
    textAlign: 'center',
    whiteSpace: 'nowrap',
  },
  ocrText: {
    color: '#ffffff',
    fontFamily: 'Open Sans, Source Sans Pro',
    fontSize: 16,
    fontWeight: '400',
    lineHeight: 1.4,
    textAlign: 'center',
    marginTop: 20,
  },
});

export default Acceuil;
